import React from 'react'
import axios from "axios";
import { useEffect, useState } from "react";
import * as Yup from "yup";
import { Formik, Form, Field } from "formik";
import { ErrorMessage } from 'formik';


const [patientEmail, setPatientEmail] = useState("");


function PatientLookup() {

  const getPatientEHR = (patientEmail) => {
    axios.get("http://localhost:3001/patient/getEHRInfo", 
      {
        params: { patientEmail },
        headers: {
          accessToken: sessionStorage.getItem("accessToken")
        }
      }
    ).then((response) => {
      if(response.data.error) {
        console.log("Error with getting patent EHR Info");
        console.log(response.data.error);
      } else {
        console.log(response.data);

      }
    });
  }

  return (
    <div className="PatientLookupPage">
      <h1> Patient EHR </h1>
      <div className="searchPatientButton">
          <button type="submit" onChange={(e) => {setPatientEmail(e.target.value)}}>Search Patient</button>
      </div>
    </div>
  )
}

export default PatientLookup
